import styled from "styled-components/"
import {IoMdArrowDroprightCircle} from 'react-icons/io';
import {IoArrowForwardCircle} from 'react-icons/io5';
export const HeroContainer = styled.div`
  background-color: #0c0c0c;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0 30px;
  height: 900px;
  position: relative;
  z-index: 1;

  @media screen and (max-width:300px) {
    height:400px;
  }

  &:before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;



} 
`;


export const HeroBg = styled.div`
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  height: 100%;
  width: 100%;
  overflow: hidden;

`;
export const VideoBg = styled.video`
  width: 100%;
  height: 100%;
  -o-object-fit: cover;
  object-fit: cover;
  background-color: #232a34;




`;

export const HeroContent = styled.div`
  z-index: 3;
  max-width: 1200px;
  position: absolute;
  padding: 8px 24px;
  display: flex;
  flex-direction: column;
  align-items:center;
`;

export const HeroH1 =styled.h1 `
color: white;
  font-size: 48px;
  text-align: center;
  @media screen and (max-width: 768px) {
    font-size: 40px;
  }
  @media screen and (max-width: 480px) {
    font-size:32px;

  }
`;

export const HeroP =styled.p`
margin-top:24px;
  color: white;
  font-size:24px;
  text-align:center;
  max-width: 600px;
  @media screen and (max-width: 768px) {
    font-size: 24px;
  }
  @media screen and (max-width: 480px) {
    font-size:18px;

  }

`;

export const HeroBtnWrapper = styled.div`
margin-top: 32px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

export const ArrowForward = styled(IoArrowForwardCircle)`
    margin-left: 8px;
    font-size: 20px;

`;

export const ArrowRight = styled(IoMdArrowDroprightCircle)`
  margin-left: 8px;
  font-size: 20px;
`;
